mapper.select:
SELECT c.name AS category, b.board_id AS boardId, ( SELECT COUNT(*) FROM files f WHERE f.board_id = b.board_id ) AS fileCount, b.title AS title, b.writer AS writer, b.views AS views, b.reg_date AS regDate, b.update_date AS updateDate FROM board b LEFT JOIN category c ON b.category_id = c.category_id WHERE 1=1
AND b.reg_date >= #{regDateStart}
AND b.reg_date <= #{regDateEnd}
AND c.name = #{categoryName}
AND (b.title LIKE CONCAT('%', #{keyword}, '%') OR b.content LIKE CONCAT('%', #{keyword}, '%') OR b.writer LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY b.board_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) as totalCount FROM board b LEFT JOIN category c ON b.category_id = c.category_id WHERE 1=1
AND b.reg_date >= #{regDateStart}
AND b.reg_date <= #{regDateEnd}
AND c.name = #{categoryName}
AND (b.title LIKE CONCAT('%', #{keyword}, '%') OR b.content LIKE CONCAT('%', #{keyword}, '%') OR b.writer LIKE CONCAT('%', #{keyword}, '%'))
