mapper.select:
SELECT ( SELECT c.name FROM category c WHERE b.category_id = c.category_id ) AS category, b.board_id AS boardId, ( SELECT COUNT(*) FROM files f WHERE f.board_id = b.board_id ) AS fileCount, b.title AS title, b.writer AS writer, b.views AS views, b.reg_date AS regDate, b.update_date AS updateDate FROM board b WHERE 1=1
AND b.reg_date >= #{regDateStart}
AND b.reg_date <= #{regDateEnd}
AND b.category_id = ( SELECT c.category_id FROM category c WHERE c.name = #{categoryName} )
AND (b.title LIKE CONCAT('%', #{titleAndContentKeyword}, '%') OR b.content LIKE CONCAT('%', #{titleAndContentKeyword}, '%'))
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) as totalCount FROM board b WHERE 1=1
AND b.reg_date >= #{regDateStart}
AND b.reg_date <= #{regDateEnd}
AND b.category_id = ( SELECT c.category_id FROM category c WHERE c.name = #{categoryName} )
AND (b.title LIKE CONCAT('%', #{titleAndContentKeyword}, '%') OR b.content LIKE CONCAT('%', #{titleAndContentKeyword}, '%'))
